import React, {useContext} from 'react';
import { CredentialsContext } from '../context/CredentialsContext';
import useComponent from '../hooks/useComponent';

const Profile = () => {
    const {storedCredentials, setStoredCredentials} = useContext(CredentialsContext)
    const { FlexRowCentreDiv, FlexColumnCentreDiv, FlexRowSpaceAroundDiv } = useComponent()
    const {name, followers, following} = storedCredentials;
    return (
        <div>
            <FlexColumnCentreDiv>
                <div style={{display: 'flex', justifyContent: 'space-around', alignItems: 'center', flexDirection: 'row'}}>
                    <h1>{name}</h1>
                    <FlexColumnCentreDiv>
                        <h1 style={{margin: 0}}>{followers.length}</h1>
                        <h3 style={{margin: 0}}>Followers</h3>
                    </FlexColumnCentreDiv>
                    <FlexColumnCentreDiv>
                        <h1 style={{margin: 0}}>{following.length}</h1>
                        <h3 style={{margin: 0}}>Following</h3>
                    </FlexColumnCentreDiv>
                </div>
            </FlexColumnCentreDiv>
        </div>
    )
}

export default Profile;
