import React, {useContext} from "react";
import { CredentialsContext } from "../context/CredentialsContext";

const SettingsButton = ({text, onClick}) => (
    <button onClick={onClick} style={{width: '50vw', height: '10vh', marginTop: '2vh', border: '1px solid black', borderRadius: 10}}>{text}</button>
)

const Settings = () => {
    const {storedCredentials, setStoredCredentials} = useContext(CredentialsContext)

    const Logout = () => {
        localStorage.removeItem('SebMediaCredentials')
        setStoredCredentials(null)
    }

    return (
        <div style={{display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'column'}}>
            <SettingsButton text="Logout" onClick={Logout}/>
        </div>
    )
}

export default Settings;
