import React, {useContext, useState} from 'react';
import { Outlet, NavLink, Navigate } from 'react-router-dom'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faHouse, faChartLine, faPlus, faGear, faMoon, faSun } from '@fortawesome/free-solid-svg-icons'
import './App.css'
import { CredentialsContext } from './context/CredentialsContext';
import { DarkModeContext } from './context/DarkModeContext';
import useComponent from './hooks/useComponent';
import Button from '@mui/material/Button';
import Menu from '@mui/material/Menu';
import MenuItem from '@mui/material/MenuItem';

function App() {
  const {storedCredentials, setStoredCredentials} = useContext(CredentialsContext);
  const {darkMode, setDarkMode} = useContext(DarkModeContext);
  const {Div} = useComponent()
  const [anchorEl, setAnchorEl] = useState(null);
  const open = Boolean(anchorEl);
  const handleClick = (event) => {
    console.log(event.currentTarget)
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };

  const Logout = () => {
    handleClose()
    localStorage.removeItem('SebMediaCredentials')
    setStoredCredentials(null)
  }

  const navStyle = {
    marginRight: '5%',
    fontSize: 35
  }

  const darkLightModeIconStyle = {
    fontSize: 35,
    marginLeft: 10,
    cursor: 'pointer'
  }
  return (
    <Div>
      <header style={{borderBottomColor: darkMode ? 'white' : 'black'}}>
          <Button
            id="basic-button"
            aria-controls={open ? 'basic-menu' : undefined}
            aria-haspopup="true"
            aria-expanded={open ? 'true' : undefined}
            onClick={handleClick}
            variant="text"
          >
            <FontAwesomeIcon icon={faGear} style={navStyle}/>
          </Button>
          <Menu
            id="basic-menu"
            anchorEl={anchorEl}
            open={open}
            onClose={handleClose}
            MenuListProps={{
              'aria-labelledby': 'basic-button',
            }}
          >
            <MenuItem onClick={Logout}>Logout</MenuItem>
          </Menu>
      </header>
      {storedCredentials ? <Outlet/> : <Navigate to="login"/>}
    </Div>
  );
}

export default App;
