import { useContext } from "react";
//EVENTUALLY ADD THE ABILITY TO USE LIGHT AND DARK MODE WITH USECONTEXT

const useComponent = () => {
    const FlexRowCentreDiv = (props) => {
        return <div style={{display: 'flex', justifyContent: 'center', alignItems: 'center', flexDirection: 'row'}}>{props.children}</div>
    }

    const FlexColumnCentreDiv = (props) => {
        return <div style={{display: 'flex', justifyContent: 'center', flexDirection: 'column'}}>{props.children}</div>
    }

    const FlexRowSpaceAroundDiv = (props) => {
        return <div style={{display: 'flex', justifyContent: 'space-around', alignItems: 'center', flexDirection: 'row'}}>{props.children}</div>
    }

    return {
        FlexRowCentreDiv,
        FlexColumnCentreDiv,
        FlexRowSpaceAroundDiv
    }
}

export default useComponent;
