import React, {useContext} from 'react';
import { Outlet, NavLink, Navigate } from 'react-router-dom'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faHouse, faChartLine, faPlus, faGear, faMoon, faSun } from '@fortawesome/free-solid-svg-icons'
import './App.css'
import { CredentialsContext } from './context/CredentialsContext';
import { DarkModeContext } from './context/DarkModeContext';

function App() {
  const {storedCredentials, setStoredCredentials} = useContext(CredentialsContext);
  const {darkMode, setDarkMode} = useContext(DarkModeContext);

  const navStyle = {
    marginRight: '5%'
  }

  const darkLightModeIconStyle = {
    fontSize: 35,
    marginLeft: 10,
    cursor: 'pointer'
  }
  return (
    <div>
      <header>
        <div>
          <h1>SebMedia</h1>
          {darkMode ?
            <FontAwesomeIcon icon={faSun} style={darkLightModeIconStyle} onClick={() => {alert('Coming soon')}}/>
          :
            <FontAwesomeIcon icon={faMoon} style={darkLightModeIconStyle} onClick={() => {alert('Coming soon')}}/>
          }
        </div>
        <div>
          <NavLink to="home" style={navStyle}>
            <FontAwesomeIcon icon={faHouse}/>
          </NavLink>
          <NavLink to="profile" style={navStyle}>
            <FontAwesomeIcon icon={faChartLine}/>
          </NavLink>
          <NavLink to="posts" style={navStyle}>
            <FontAwesomeIcon icon={faPlus} style={navStyle}/>
          </NavLink>
          <NavLink to="settings" style={navStyle}>
            <FontAwesomeIcon icon={faGear} style={navStyle}/>
          </NavLink>
        </div>
      </header>
      {storedCredentials ? <Outlet/> : <Navigate to="login"/>}
    </div>
  );
}

export default App;
